import { useState } from "react";
import { useNavigate } from "react-router-dom";
import NavbarComponent from "../components/Navbar";

const LoginPage = () => {
    // navigate
    const navigate = useNavigate()

    // states
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errorUsername, setErrorUsername] = useState("")
    const [errorPassword, setErrorPassword] = useState("")

    // handle submit
    const handleSubmit = () => {
        if (username || password) {
            if (username) {
                setErrorUsername("")
            }
            if (password) {
                if (password.length >= 8) {
                    setErrorPassword("")
                } else {
                    setErrorPassword("Please enter valid password")
                }
            }
            if (username && password) {
                if (password.length >= 8) {
                    const admin = localStorage.getItem("admin")
                    const st_username = admin
                    const st_password = "test1234"
                    if (st_username === username) {
                        setErrorUsername("")
                        if (st_password === password) {
                            setErrorPassword("")
                            navigate("/")
                        } else {
                            setErrorPassword("Invalid password")
                        }
                    } else {
                        setErrorUsername("Invalid username")
                    }
                }
            }
        } else {
            if (username === "") {
                setErrorUsername("Please enter username")
            }
            if (password === "") {
                setErrorPassword("Please enter password")
            }
        }
    };

    // return
    return (
        <>
            <NavbarComponent now={''} />
            <div 
            className="flex w-full h-screen justify-center items-center bg-gray-900"

            >
                <div className="w-96 bg-gray-700 rounded-lg px-10 py-10">
                    <h1 className="text-3xl font-bold text-center mt-3 mb-6 text-white">Login</h1>
                    <input
                        type="text"
                        id="username"
                        placeholder="Enter username"
                        className={`shadow appearance-none border bg-gray-900 rounded-md w-full py-2 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-4 ${errorUsername ? 'border-red-500' : 'border-gray-500'}`}
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    {
                        errorUsername && <p className="text-red-500 text-xs">{errorUsername}</p>
                    }
                    <input
                        type="password"
                        id="password"
                        placeholder="Enter password"
                        className={`shadow appearance-none border bg-gray-900 rounded-md w-full py-2 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-4 ${errorPassword ? 'border-red-500' : 'border-gray-500'}`}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    {
                        errorPassword && <p className="text-red-500 text-xs">{errorPassword}</p>
                    }
                    <button onClick={handleSubmit} className="w-full border-2 border-yellow-500 hover:bg-yellow-500 text-white hover:text-black text-md font-bold py-2 px-4 rounded-md focus:outline-none focus:shadow-outline mt-8 mb-8">SUBMIT</button>
                </div>
            </div>
        </>
    );
}

export default LoginPage;
