import { useEffect, useState } from "react";
import NavbarComponent from "../components/Navbar";
import SidebarComponent from "../components/Sidebar";
import Dropdown from "../components/Dropdown";


const OrdersPage = () => {
    // states
    const [refresh, setRefresh] = useState(false)
    const [selectedValue, setSelectedValue] = useState("");
    const [orders, setOrders] = useState()

    // use effect
    useEffect(() => {
        console.log('Refreshed');
    }, [refresh]);

    // status change
    const changeStatus = (index, status) => {
        orders[index].status = status
        setOrders(orders)
        setRefresh(!refresh)
    }

    // timestamp foramat
    const formatTimestamp = (timestamp) => {
        const date = new Date(timestamp);

        // Extract the components
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
        const year = date.getFullYear();
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');

        // Format as dd/mm/yyyy 00:00
        return `${day}/${month}/${year} - ${hours}:${minutes}`;
    };

    // return
    return (
        <>
            <NavbarComponent now={'orders'} />
            <SidebarComponent now={'orders'} />
            <div className="w-auto min-h-screen px-3 pt-16 md:ml-60 lg:ml-80 bg-gray-900">
                <div className="w-full min-h-screen bg-gray-700 rounded-lg px-5 py-5 mt-3">
                    {/* Filters */}
                    <div className="mb-3 flex w-full items-center">
                        {/* Dropdown Start */}
                        <div className="w-1/2 flex">
                            <Dropdown key_name={"category"} items={["Beverages", "Snacks", "Breakfast", "Main Course"]} selectedValue={selectedValue} setSelectedValue={setSelectedValue} />
                        </div>
                        {/* Dropdown End */}
                        {/* Search Bar Start */}
                        <div className="w-1/2 flex justify-end">
                            <input
                                type="text"
                                id="search"
                                placeholder="Search..."
                                className="flex px-4 w-7/12 lg:w-4/12 py-1 bg-gray-900 border border-gray-300 rounded-full text-white text-xs lg:text-sm"
                                // value={search}
                                onChange={(e) => search(e.target.value)}
                            />
                        </div>
                        {/* Search Bar End */}
                    </div>
                    {/* Table */}
                    <div className="w-full bg-gray-900 overflow-x-auto rounded-xl">
                        <table className="w-full table-auto border-collapse border border-gray-400">
                            <thead>
                                <tr>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Order ID
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Order Items
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Total Amount
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Order Time
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Status
                                    </th>
                                </tr>
                            </thead>
                            <tbody>
                                {orders.map((item, index) => (
                                    <tr key={index}>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            ORDER_ID: #{item.order_id}
                                        </td>
                                        <td onClick={() => console.log("dddd")} className="border text-white text-sm border-gray-500 px-4 py-2 hover:bg-gray-800 active:bg-gray-700">
                                            {item.order_items.length > 1 ? item.order_items[0]?.name + " . . ." : item.order_items[0]?.name }
                                        </td>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            ₹{item.order_total_amount}
                                        </td>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            {formatTimestamp(parseInt(item.order_time))}
                                        </td>
                                        <td className={`border border-gray-500 px-4 py-2 text-center`}>
                                            {/* {item.status} */}
                                            <select className={`w-full text-xs font-bold bg-gray-900 capitalize p-2 rounded-md border border-gray-500 ${
                                                        item.status === "PAYMENT PENDING" ?
                                                            "text-gray-400"
                                                        :
                                                            item.status === "PLACED" ?
                                                                "text-blue-400"
                                                            :
                                                                item.status === "PREPARING" ?
                                                                    "text-orange-400"
                                                                :
                                                                    item.status === "READY" ?
                                                                        "text-yellow-400"
                                                                    :
                                                                        item.status === "COMPLETED" ?
                                                                            "text-green-400"
                                                                        :
                                                                            "text-red-400"
                                            }`} name={"order_status"} id={"order_status"} value={item.status} onChange={(e) => changeStatus(index, e.target.value)}>
                                                <option value="">all</option>
                                                {["PAYMENT PENDING", "PLACED", "PREPARING", "READY", "COMPLETED", "CANCELLED"].map((item) => (
                                                    <option className="text-center" key={item} value={item}>{item}</option>

                                                ))}
                                            </select>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {/* Table */}
                </div>
            </div>
        </>
    );
}

export default OrdersPage;
