import React, { useState } from 'react';
import { faTachometerAlt, faUsers, faChartBar, faBook } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useNavigate } from 'react-router-dom';

function SidebarComponent({ now }) {
    // navigate
    const navigate = useNavigate()

    // states
    const [current, setCurrent] = useState(now)

    // return
    return (
        <div className="hidden md:block m lg:w-full max-w-80 min-h-screen fixed border border-right p-3 mt-16">
            <div className="w-full min-h-screen bg-gray-200 rounded-lg px-5 py-5">
                <div onClick={() => {setCurrent('dashboard'); navigate('/')}} className={current == "dashboard" ? "flex flex-row w-full bg-gray-800 rounded-md mt-4 px-5 py-2" : "flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2"}>
                    <FontAwesomeIcon className="mt-1" icon={faTachometerAlt} color={current == "dashboard" ? "white" : "black"} size="md" />
                    <h1 className={current == "dashboard" ? "text-md font-semibold text-white ml-2" : "text-md font-semibold text-black ml-2"}>Dashboard</h1>
                </div>
                <div onClick={() => {setCurrent('users'); navigate('/users')}} className={current == "users" ? "flex flex-row w-full bg-gray-800 rounded-md mt-4 px-5 py-2" : "flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2"}>
                    <FontAwesomeIcon className="mt-1" icon={faUsers} color={current == "users" ? "white" : "black"} size="md" />
                    <h1 className={current == "users" ? "text-md font-semibold text-white ml-2" : "text-md font-semibold text-black ml-2"}>Users</h1>
                </div>
                <div onClick={() => {setCurrent('categories'); navigate('/categories')}} className={current == "categories" ? "flex flex-row w-full bg-gray-800 rounded-md mt-4 px-5 py-2" : "flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2"}>
                    <FontAwesomeIcon className="mt-1" icon={faChartBar} color={current == "categories" ? "white" : "black"} size="md" />
                    <h1 className={current == "categories" ? "text-md font-semibold text-white ml-2" : "text-md font-semibold text-black ml-2"}>Categories</h1>
                </div>
                <div onClick={() => {setCurrent('books'); navigate('/books')}} className={current == "books" ? "flex flex-row w-full bg-gray-800 rounded-md mt-4 px-5 py-2" : "flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2"}>
                    <FontAwesomeIcon className="mt-1" icon={faBook} color={current == "books" ? "white" : "black"} size="md" />
                    <h1 className={current == "books" ? "text-md font-semibold text-white ml-2" : "text-md font-semibold text-black ml-2"}>Books</h1>
                </div>
            </div>
        </div>
    );
}

export default SidebarComponent;
