import React, { useState } from "react";

const Dropdown = ({ key_name, items, setSelectedV }) => {
    const [selectedValue, setSelectedValue] = useState("");

    const handleChange = (event) => {
        setSelectedValue(event.target.value);
    };

    return (
        <div>
            {/* <h1>React Dropdown Example</h1> */}
            <label className="text-sm" htmlFor="dropdown">Select {key_name}: </label>
            <select className="text-xs p-1 rounded" id="dropdown" value={selectedValue} onChange={handleChange}>
                <option className="text-xs" value="">--Select an option--</option>
                {/* <option className="text-xs" value="option1">Option 1</option>
                <option className="text-xs" value="option2">Option 2</option>
                <option className="text-xs" value="option3">Option 3</option> */}
                {items.map((item) => (
                    <option className="text-xs" value={item}>{item}</option>
                ))}
            </select>
            {selectedValue && <p className="text-sm">You selected: {selectedValue}</p>}
        </div>
    );
};

export default Dropdown;
