import { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
    // navigate
    const navigate = useNavigate()

    // states
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errorUsername, setErrorUsername] = useState("")
    const [errorPassword, setErrorPassword] = useState("")

    // handle submit
    const handleSubmit = () => {
        if (username && password) {
            navigate('dashboard')
        } else {
            if (username == "") {
                setErrorUsername("Please enter username")
            }
            if (password == "") {
                setErrorPassword("Please enter password")
            }
        }
    };

    // return
    return (
        <>
            <div className="flex w-full h-screen justify-center items-center">
                <div className="w-1/3 bg-gray-200 rounded-lg px-32 pt-10 pb-10">
                    <h1 className="text-3xl font-bold text-center mt-3 mb-6">Login</h1>
                    <input
                        type="text"
                        id="username"
                        placeholder="Enter username"
                        className={`shadow appearance-none border rounded-md w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline mt-4 ${errorUsername ? 'border-red-500' : 'border-gray-300'}`}
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    {
                        errorUsername && <p className="text-red-500 text-sm">{errorUsername}</p>
                    }
                    <input
                        type="password"
                        id="password"
                        placeholder="Enter password"
                        className={`shadow appearance-none border rounded-md w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline mt-4 ${errorPassword ? 'border-red-500' : 'border-gray-300'}`}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    {
                        errorPassword && <p className="text-red-500 ">{errorPassword}</p>
                    }
                    <button onClick={handleSubmit} className="w-full bg-gray-800 hover:bg-gray-700 text-white text-md font-bold py-2 px-4 rounded-md focus:outline-none focus:shadow-outline mt-8 mb-8">SUBMIT</button>
                </div>
            </div>
        </>
    );
}

export default LoginPage;
