    // use effect
    useEffect(() => {
        console.log('Refreshed');
    }, [refresh]);

    // status change
    const changeStatus = (index, status) => {
        students[index].status = status
        setStudents(students)
        setRefresh(!refresh)
    }

    // search
    const search = (value) => {
        if (value) {
            const lowercase_value = value.toLowerCase();
            const filterd_students = all_students.filter(students => students.name.toLowerCase().includes(lowercase_value) || students.mobile_no.toLowerCase().includes(lowercase_value));
            setStudents(filterd_students)
            setRefresh(!refresh)
        } else {
            setStudents(all_students)
            setRefresh(!refresh)
        }
    }

    // filter
    const filter = (value) => {
        let filterd_students = all_students
        if (selectedValue.department) {
            const department_filter = filterd_students.filter(student => student.department.toLowerCase() === selectedValue.department);
            filterd_students = [...filterd_students, department_filter]
        }
        if (selectedValue.year) {
            const year_filter = filterd_students.filter(student => student.year.toLowerCase() === selectedValue.year);
            filterd_students = [...filterd_students, year_filter]
        }
        if (selectedValue.gender) {
            const gender_filter = filterd_students.filter(student => student.gender.toLowerCase() === selectedValue.gender);
            filterd_students = [...filterd_students, gender_filter]
        }
        if (selectedValue.status) {
            const status_filter = filterd_students.filter(student => student.status.toLowerCase() === selectedValue.status);
