                                <button onClick={() => setShowFilter(false)} className="mt-4 mb-2 text-gray-300 hover:text-white text-xs rounded font-semibold capitalize">
                                    Hide Filters
                                    <FontAwesomeIcon className="ml-1 hover:text-white" icon={faAngleUp} color="#D1D5DB" size="md" />
                                </button>
                                :
                                <button onClick={() => setShowFilter(true)} className="mt-4 mb-2 text-gray-300 hover:text-white text-xs rounded font-semibold capitalize">
                                    Show Filters
                                    <FontAwesomeIcon className="ml-1 hover:text-white" icon={faAngleDown} color="#D1D5DB" size="md" />
                                </button>
                            }
                        </div>
                        {/* Filter Box Button End */}
                        {/* Search Bar Start */}
                        <div className="w-1/2 flex justify-end">
                            <input
                                type="text"
                                id="search"
                                placeholder="Search..."
                                className="flex px-4 w-7/12 lg:w-4/12 py-1 bg-gray-900 border border-gray-300 rounded-full text-white text-xs lg:text-sm"
                                // value={search}
                                onChange={(e) => search(e.target.value)}
                            />
                        </div>
                        {/* Search Bar End */}
                        {/* Filter box Start */}
                        {
                            showFilter &&
                            <div className="absolute top-10 left-0 w-full bg-gray-900 border border-gray-600 z-40 rounded-lg">
                                <div className="w-full p-4">
                                    {/* content inside the filter box */}
                                    {/* dropdown */}
                                    <div className="w-full grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
                                        <Dropdown key_name={"department"} items={["Mechanical", "Electronics", "Computer"]} selectedValue={selectedValue.department} setSelectedValue={setSelectedValue} />
                                        <Dropdown key_name={"year"} items={["First", "Second", "Third"]} selectedValue={selectedValue.year} setSelectedValue={setSelectedValue} />
                                        <Dropdown key_name={"gender"} items={["male", "female", "other"]} selectedValue={selectedValue.gender} setSelectedValue={setSelectedValue} />
                                        <Dropdown key_name={"status"} items={["Active", "Inactive"]} selectedValue={selectedValue.status} setSelectedValue={setSelectedValue} />
                                    </div>
                                    {/* button */}
                                    <div className="flex justify-center mt-4">
                                        <button onClick={filter} className="border-2 border-blue-500 text-blue-500 hover:text-white px-8 py-1.5 text-xxs rounded font-bold uppercase hover:bg-blue-500 active:bg-blue-600">
                                            Search
                                        </button>
                                    </div>
                                </div>
                            </div>
                        }
                        {/* Filter box End */}
                    </div>
                    {/* Table Start */}
                    <div className={`w-full bg-gray-900 overflow-x-auto rounded-xl ${showFilter ? "mt-80 sm:mt-44 lg:mt-24" : ""}`}>
                        <table className="w-full table-auto border-collapse border border-gray-400">
                            <thead>
                                <tr>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Sudent ID
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Name
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Mobile No
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Age
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Gender
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Current Year
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Department
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Status
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Options
                                    </th>
                                </tr>
                            </thead>
                            <tbody>
                                {students.map((item, index) => (
                                    <tr key={index}>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2 capitalize">
                                            {item.student_id}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2 capitalize">
                                            {item.name}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.mobile_no}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.age}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.gender}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.current_year}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.department}
                                        </td>
                                        <td className="border text-white text-xs border-gray-500 px-4 py-2">
                                            <div className="w-full flex justify-center">
                                                <div onClick={() => changeStatus(index, item.status === "ACTIVE" ? "INACTIVE" : "ACTIVE")} className="w-20 bg-gray-600 rounded-full shadow-md shadow-inner">
                                                    {
                                                        item.status === "ACTIVE" ?
                                                            <div className="flex w-14 py-1 bg-green-700 rounded-full text-white font-bold ml-auto justify-center status-box">
                                                                {item.status}
                                                            </div>
                                                            :
                                                            <div className="flex w-14 py-1 bg-red-600 rounded-full text-white font-bold justify-center status-box">
                                                                {item.status}
                                                            </div>
                                                    }
                                                </div>
                                            </div>
                                        </td>
                                        <td className="border text-xs border-gray-300 w-1/12 px-4 py-2">
                                            <div className="w-full flex justify-center">
                                                <FontAwesomeIcon className="mt-1 mr-2" onClick={() => editModalOpen(index)} icon={faEdit} color="#6961ff" size="md" />
                                                <FontAwesomeIcon className="mt-1 ml-2" onClick={() => deleteModalOpen(index)} icon={faTrash} color="#f54242" size="md" />
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {/* Table End */}
                    {/* Edit Modal Start */}
                    {isEditModalOpen && (
                        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
                            <div className="bg-gray-700 p-6 rounded-lg shadow-lg w-96 relative">
                                {/* close button */}
                                <button
                                    className="absolute top-4 right-4 text-gray-300 hover:text-gray-500"
                                    onClick={() => setIsEditModalOpen(false)}
                                >
                                    &times;
                                </button>

                                {/* modal content */}
                                <h2 className="text-xl text-white text-center font-bold mb-4">Edit Student</h2>
                                {/* form */}
                                <form onSubmit={editModalSubmit}>
                                    {/* name field */}
                                    <div className="mb-4">
                                        <label htmlFor="name" className="block text-sm font-medium text-gray-300">
                                            Name
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            placeholder="Enter name"
                                            className="shadow appearance-none bg-gray-900 text-sm border border-gray-500 rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.name}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>
                                    {/* mobile no field */}
                                    <div className="mb-4">
                                        <label htmlFor="mobile_no" className="block text-sm font-medium text-gray-300">
                                            Mobile No
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="mobile_no"
                                            name="mobile_no"
                                            placeholder="Enter mobile no"
                                            className="shadow appearance-none bg-gray-900 text-sm border border-gray-500 rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.mobile_no}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>
                                    {/* age no field */}
                                    <div className="mb-4">
                                        <label htmlFor="age" className="block text-sm font-medium text-gray-300">
                                            Age
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="age"
                                            name="age"
                                            placeholder="Enter age"
                                            className="shadow appearance-none bg-gray-900 text-sm border border-gray-500 rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.age}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>
                                    {/* gender no field */}
                                    <div className="mb-4">
                                        <label htmlFor="gender" className="block text-sm font-medium text-gray-300">
                                            Gender
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <div className="flex items-center mt-2">
                                            <label className="mr-4 text-sm text-gray-300 lowercase">
                                                <input
                                                    type="radio"
                                                    id="gender"
                                                    name="gender"
                                                    value="male"
                                                    checked={formData.gender === "male"}
                                                    onChange={editModalChange}
                                                    className="mr-1"
                                                />
                                                Male
                                            </label>
                                            <label className="mr-4 text-sm text-gray-300 lowercase">
                                                <input
                                                    type="radio"
                                                    id="gender"
                                                    name="gender"
                                                    value="female"
                                                    checked={formData.gender === "female"}
                                                    onChange={editModalChange}
                                                    className="mr-1"
                                                />
                                                Female
                                            </label>
                                            <label className="text-sm text-gray-300 lowercase">
                                                <input
                                                    type="radio"
                                                    id="gender"
                                                    name="gender"
                                                    value="other"
                                                    checked={formData.gender === "other"}
                                                    onChange={editModalChange}
                                                    className="mr-1"
                                                />
                                                Other
                                            </label>
                                        </div>
                                    </div>

                                    {/* submit button */}
                                    <div className="flex justify-center mt-4">
                                        <button
                                            type="submit"
                                            className="mt-4 border-2 border-blue-500 text-blue-500 hover:text-white px-4 py-2 text-xxs rounded font-bold uppercase hover:bg-blue-500"
                                        >
                                            Submit
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}
                    {/* Edit Modal End */}
                    {/* Delete Modal Start */}
                    {isDeleteModalOpen && (
                        <div className="fixed inset-0 bg-gray-900 bg-opacity-50 flex justify-center items-center">
                            <div className="bg-gray-700 p-6 rounded shadow-lg w-96 relative">
                                {/* close button */}
                                <button
                                    className="absolute top-4 right-4 text-gray-300 hover:text-gray-500"
                                    onClick={() => setIsDeleteModalOpen(false)}
                                >
                                    &times;
                                </button>
                                {/* model content */}
                                <h2 className="text-lg text-white font-bold mb-2">Are you sure?</h2>
                                <p className="mb-7 text-sm text-gray-300">Do you want to delete this students data?</p>
                                {/* yes or no button */}
                                <div className="flex justify-center space-x-4">
                                    <button
                                        onClick={() => setIsDeleteModalOpen(false)}
                                        className="border-2 border-red-500 text-red-500 hover:text-white py-2 px-4 text-xxs font-bold uppercase rounded hover:bg-red-500"
                                    >
                                        No
                                    </button>
                                    <button
                                        onClick={deleteModalConfirm}
                                        className="border-2 border-green-500 text-green-500 hover:text-white py-2 px-4 text-xxs font-bold uppercase rounded hover:bg-green-500"
                                    >
                                        Yes
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                    {/* Delete Modal End */}
                </div>
            </div>
        </>
    );
}

export default StudentsPage;
