import { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
    // navigate
    const navigate = useNavigate()

    // states
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")

    // handle submit
    const handleSubmit = () => {
        if (username && password) {
