        } else {

        }
    };

    // return
    return (
        <>
            <div className="flex w-full h-screen justify-center items-center">
                <div className="w-1/3 bg-gray-200 rounded-lg px-32 py-5">
                    <h1 className="text-3xl font-bold text-center mt-3 mb-6">Login</h1>
                    <input
                        type="text"
                        id="username"
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline mt-4"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        id="password"
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline mt-4"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={handleSubmit}>Submit</button>
                </div>
            </div>
        </>
    );
}

export default LoginPage;
