import { useEffect, useState } from "react";
import NavbarComponent from "../components/Navbar";
import SidebarComponent from "../components/Sidebar";


const BooksPage = () => {
    // states
    const [refresh, setRefresh] = useState(false)
    const [books, setBooks] = useState([
        {
            "book_image": "data:image/jpeg;base64,",
            "book_name": "Book 1",
            "category_name": "Category 2",
            "status": "ACTIVE"
        },
        {
            "book_image": "data:image/jpeg;base64,",
            "book_name": "Book 2",
            "category_name": "Category 1",
            "status": "ACTIVE"
        },
        {
            "book_image": "data:image/jpeg;base64,",
            "book_name": "Book 3",
            "category_name": "Category 6",
            "status": "ACTIVE"
        },
        {
            "book_image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR3tufV9v3QaDl5gfFql4JITgj-awlcUg9IHp-4JDnqJquXwFM0snUTd9kCAA&s",
            "book_name": "Book 4",
            "category_name": "Category 6",
            "status": "INACTIVE"
        },
        {
            "book_image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRhaauaqFbZ4EGEi8_u6Q-M4_yglMJYtbfcQU9gJEIs1YUwfw5gwuVuDjIDoQ&s",
            "book_name": "Book 5",
            "category_name": "Category 5",
            "status": "ACTIVE"
        },
        {
            "book_image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQCvEGnZAusA6szh03JPZ7zByl1LR5l2vUPBG30C57tupBy6JP0A2I9OMsCHA&s",
            "book_name": "Book 6",
            "category_name": "Category 3",
            "status": "INACTIVE"
        },
        {
            "book_image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr3ODtK8HPnINXTY5-Aet7bQWg-VgZnY4MBd3oWC7QFzxUT-iSwBnD7BLiMw&s",
            "book_name": "Book 7",
            "category_name": "Category 7",
            "status": "ACTIVE"
        },
        {
            "book_image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr3ODtK8HPnINXTY5-Aet7bQWg-VgZnY4MBd3oWC7QFzxUT-iSwBnD7BLiMw&s",
            "book_name": "Book 8",
            "category_name": "Category 4",
            "status": "ACTIVE"
        }
    ])

    // use effect
    useEffect(() => {
        console.log('Refreshed');
    }, [refresh]);

    // status change
    const changeStatus = (index, status) => {
        books[index].status = status
        setBooks(books)
        setRefresh(!refresh)
    }

    // return
    return (
        <>
            <NavbarComponent />
            <SidebarComponent now={'books'} />
            <div className="w-10/12 min-h-screen px-3 pt-16 ml-80">
                <div className="w-full min-h-screen bg-gray-200 rounded-lg px-5 py-5 mt-3">
                    {/* Table */}
                    <div className="w-full bg-gray-100">
                        <table className="w-full table-auto border-collapse border border-gray-400">
                            <thead>
                                <tr>
                                    <th className="text-white text-md border border-gray-300 px-4 py-2 bg-gray-800 capitalize">
                                        No
                                    </th>
                                    <th className="text-white text-md border border-gray-300 px-4 py-2 bg-gray-800 capitalize">
                                        Book Image
                                    </th>
                                    <th className="text-white text-md border border-gray-300 px-4 py-2 bg-gray-800 capitalize">
                                        Book Name
                                    </th>
                                    <th className="text-white text-md border border-gray-300 px-4 py-2 bg-gray-800 capitalize">
                                        Category Name
                                    </th>
                                    <th className="text-white text-md border border-gray-300 px-4 py-2 bg-gray-800 capitalize">
                                        Status
                                    </th>
                                </tr>
                            </thead>
                            <tbody>
                                {books.map((item, index) => (
                                    <tr key={index}>
                                        <td className="border text-sm border-gray-300 px-4 py-2">
                                            {index + 1}
                                        </td>
                                        <td className="border text-sm border-gray-300 px-4 py-2">
                                            <img src={item.book_image} alt="Example" className="rounded-md shadow-lg w-12" />
                                        </td>
                                        <td className="border text-sm border-gray-300 px-4 py-2">
                                            <img src={item.book_name} alt="Example" className="rounded-md shadow-lg w-12" />
                                        </td>
                                        <td className="border text-sm border-gray-300 px-4 py-2">
                                            {item.category_name}
                                        </td>
                                        <td className="border text-xs border-gray-300 px-4 py-2">
                                            <div className="w-full flex justify-center">
                                                <div onClick={() => changeStatus(index, item.status === "ACTIVE" ? "INACTIVE" : "ACTIVE")} className="w-20 bg-gray-300 rounded-full shadow-md shadow-inner">
                                                    {
                                                        item.status === "ACTIVE" ?
                                                            <div className="flex w-14 py-1 bg-green-700 rounded-full text-white font-bold ml-auto justify-center status-box">
                                                                {item.status}
                                                            </div>
                                                            :
                                                            <div className="flex w-14 py-1 bg-red-600 rounded-full text-white font-bold justify-center status-box">
                                                                {item.status}
                                                            </div>
                                                    }
                                                </div>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {/* Table */}
                </div>
            </div>
        </>
    );
}

export default BooksPage;
