import React, { useState } from "react";

const Dropdown = ({ key_name, items, selectedValue, setSelectedValue }) => {
    // selected value setting
    const handleChange = (event) => {
        setSelectedValue(event.target.value);
    };

    // return
    return (
        <div>
            {/* <h1>React Dropdown Example</h1> */}
            <label className="text-sm text-white" htmlFor="dropdown">Select {key_name}: </label>
            <select className="text-xs text-white bg-gray-900 capitalize p-1 rounded-md border border-gray-500" id="dropdown" value={selectedValue} onChange={handleChange}>
                <option value="">all</option>
                {items.map((item) => (
                    <option value={item}>{item}</option>
                ))}
            </select>
        </div>
    );
};

export default Dropdown;
