import { CanteenApi } from "../constant/constant";

////////////////////////////////////////////////////////// AUTH //////////////////////////////////////////////////////////

export const CanteenLoginApi = async (form_data) => {
    try {
        const { data } = await CanteenApi.post('/login', form_data)
        return data;
    } catch (error) {
        return false
    }
}

//////////////////////////////////////////////////////////

export const CanteenAuthApi = async (token) => {
    try {
        const { data } = await CanteenApi.post('/auth', {}, { headers: { "canteentoken": token } })
        return data;
    } catch (error) {
        return false
    }
}

////////////////////////////////////////////////////////// STUDENT //////////////////////////////////////////////////////////

export const StudentList = async (token) => {
    try {
        const { data } = await CanteenApi.get('/student/list', { headers: { "canteentoken": token } })
        return data;
    } catch (error) {
        return false
    }
}

////////////////////////////////////////////////////////// MENU //////////////////////////////////////////////////////////

export const MenuCreate = async (token, form_data) => {
    try {
        const { data } = await CanteenApi.post('/menu/create', form_data, { headers: { "canteentoken": token } })
        return data;
    } catch (error) {
        return false
    }
}

//////////////////////////////////////////////////////////

export const MenuList = async (token) => {
    try {
        const { data } = await CanteenApi.get('/menu/list', { headers: { "canteentoken": token } })
        return data;
    } catch (error) {
        return false
    }
}

//////////////////////////////////////////////////////////

export const MenuUpdate = async (token, form_data) => {
    try {
        const { data } = await CanteenApi.patch('/menu/list', form_data, { headers: { "canteentoken": token } })
        return data;
    } catch (error) {
        return false
    }
}

//////////////////////////////////////////////////////////

export const menuDelete = async (token, _id) => {
    try {
        const { data } = await CanteenApi.delete('/menu/delete?id=${}', { headers: { "canteentoken": token } })
        return data;
    } catch (error) {
        return false
    }
}
