import React, { useState } from "react";

// this type example (success, info, warning, error)
const PopupAlert = ({ message, type, onClose }) => {
    return (
      <div
        className={`fixed top-10 right-5 w-64 p-4 rounded-lg shadow-lg transition-transform duration-300 ${
          type === "success" ? "bg-green-500" : "bg-red-500"
        } text-white`}
      >
        <div className="flex justify-between items-center">
          <span>{message}</span>
          <button
            className="ml-4 text-xl font-bold"
            onClick={onClose}
          >
            &times;
          </button>
        </div>
      </div>
    );
  };

export default PopupAlert;
