import { useEffect, useState } from "react";
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import NavbarComponent from "../components/Navbar";
import SidebarComponent from "../components/Sidebar";
import Dropdown from "../components/Dropdown";


const UsersPage = () => {
    const all_users = [
        {
            "name": "irfad",
            "mobile_no": "0000000000",
            "age": "22",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "vicki",
            "mobile_no": "1111111111",
            "age": "25",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "kirthi",
            "mobile_no": "2222222222",
            "age": "30",
            "gender": "other",
            "status": "ACTIVE"
        },
        {
            "name": "rahman",
            "mobile_no": "3333333333",
            "age": "22",
            "gender": "male",
            "status": "INACTIVE"
        },
        {
            "name": "akif",
            "mobile_no": "4444444444",
            "age": "23",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "adithya",
            "mobile_no": "5555555555",
            "age": "19",
            "gender": "female",
            "status": "ACTIVE"
        },
        {
            "name": "irshad",
            "mobile_no": "6666666666",
            "age": "48",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "arya",
            "mobile_no": "7777777777",
            "age": "50",
            "gender": "female",
            "status": "ACTIVE"
        },
        {
            "name": "salman",
            "mobile_no": "8888888888",
            "age": "27",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "akshaya",
            "mobile_no": "9999999999",
            "age": "36",
            "gender": "female",
            "status": "ACTIVE"
        },
        {
            "name": "vishnu",
            "mobile_no": "1010101010",
            "age": "39",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "gopal",
            "mobile_no": "1212121212",
            "age": "20",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "risal",
            "mobile_no": "1313131313",
            "age": "43",
            "gender": "male",
            "status": "INACTIVE"
        },
        {
            "name": "bilal",
            "mobile_no": "1414141414",
            "age": "40",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "fathima",
            "mobile_no": "1515151515",
            "age": "32",
            "gender": "female",
            "status": "ACTIVE"
        },
        {
            "name": "majeed",
            "mobile_no": "1616161616",
            "age": "30",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "raees",
            "mobile_no": "1717171717",
            "age": "19",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "aflaha",
            "mobile_no": "1818181818",
            "age": "15",
            "gender": "female",
            "status": "ACTIVE"
        },
        {
            "name": "reshma",
            "mobile_no": "1919191919",
            "age": "31",
            "gender": "female",
            "status": "ACTIVE"
        },
        {
            "name": "mohan",
            "mobile_no": "2020202020",
            "age": "17",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "nabeel",
            "mobile_no": "2121212121",
            "age": "18",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "karthika",
            "mobile_no": "2323232323",
            "age": "16",
            "gender": "female",
            "status": "ACTIVE"
        },
        {
            "name": "lukku",
            "mobile_no": "2424242424",
            "age": "23",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "afras",
            "mobile_no": "2525252525",
            "age": "53",
            "gender": "male",
            "status": "ACTIVE"
        },
        {
            "name": "ansaf",
            "mobile_no": "2626262626",
            "age": "22",
            "gender": "male",
            "status": "ACTIVE"
        }
    ]
    // states
    const [refresh, setRefresh] = useState(false)
    const [selectedValue, setSelectedValue] = useState("");
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [index, setIndex] = useState(0);
    const [formData, setFormData] = useState({
        name: "",
        mobile_no: "",
        age: "",
        gender: ""
    });
    const [users, setUsers] = useState(all_users)

    // use effect
    useEffect(() => {
        console.log('Refreshed');
    }, [refresh]);

    // male female filtering
    useEffect(() => {
        if (selectedValue) {
            const filterd_users = all_users.filter(user => user.gender.toLowerCase() === selectedValue);
            setUsers(filterd_users)
            setRefresh(!refresh)
        } else {
            setUsers(all_users)
            setRefresh(!refresh)
        }
    }, [selectedValue]);

    // status change
    const changeStatus = (index, status) => {
        users[index].status = status
        setUsers(users)
        setRefresh(!refresh)
    }

    // search
    const search = (value) => {
        if (value) {
            const lowercase_value = value.toLowerCase();
            const filterd_users = all_users.filter(user => user.name.toLowerCase().includes(lowercase_value) || user.mobile_no.toLowerCase().includes(lowercase_value));
            setUsers(filterd_users)
            setRefresh(!refresh)
        } else {
            setUsers(all_users)
            setRefresh(!refresh)
        }
    }

    // edit modal open
    const editModalOpen = (index) => {
        setIndex(index)
        setIsEditModalOpen(true)
        setFormData(users[index]);
    }

    // edit modal change
    const editModalChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    // edit modal submit
    const editModalSubmit = (e) => {
        e.preventDefault();
        console.log("Form Data Submitted:", formData);
        users[index] = formData
        setUsers(users)
        setRefresh(!refresh)
        setIsEditModalOpen(false)
    };

    // delete modal open
    const deleteModalOpen = (index) => {
        setIndex(index)
        setIsDeleteModalOpen(true)
    }

    // edit modal confirm
    const deleteModalConfirm = () => {
        users.splice(index, 1);
        setUsers(users)
        setRefresh(!refresh)
        setIsDeleteModalOpen(false)
    };

    // return
    return (
        <>
            <NavbarComponent now={'users'} />
            <SidebarComponent now={'users'} />
            <div className="w-auto min-h-screen px-3 pt-16 md:ml-60 lg:ml-80 bg-gray-900">
                <div className="w-full min-h-screen bg-gray-700 rounded-lg px-5 py-5 mt-3">
                    <div className="mb-3 flex w-full items-center">
                        {/* Dropdown Start */}
                        <div className="w-1/2 flex">
                            <Dropdown key_name={"status"} items={["male", "female", "other"]} selectedValue={selectedValue} setSelectedValue={setSelectedValue} />
                        </div>
                        {/* Dropdown End */}
                        {/* Search Bar Start */}
                        <div className="w-1/2 flex justify-end">
                            <input
                                type="text"
                                id="search"
                                placeholder="Search..."
                                className="flex px-4 w-7/12 lg:w-4/12 py-1 bg-gray-900 border border-gray-300 rounded-full text-white text-xs lg:text-sm"
                                // value={search}
                                onChange={(e) => search(e.target.value)}
                            />
                        </div>
                        {/* Search Bar End */}
                    </div>
                    {/* Table Start */}
                    <div className="w-full bg-gray-900 overflow-x-auto rounded-xl">
                        <table className="w-full table-auto border-collapse border border-gray-400">
                            <thead>
                                <tr>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Name
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Mobile No
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Age
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Gender
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Status
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Options
                                    </th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map((item, index) => (
                                    <tr key={index}>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2 capitalize">
                                            {item.name}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.mobile_no}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.age}
                                        </td>
                                        <td className="border text-sm text-white text-md border-gray-500 px-4 py-2">
                                            {item.gender}
                                        </td>
                                        <td className="border text-white text-xs border-gray-500 px-4 py-2">
                                            <div className="w-full flex justify-center">
                                                <div onClick={() => changeStatus(index, item.status === "ACTIVE" ? "INACTIVE" : "ACTIVE")} className="w-20 bg-gray-600 rounded-full shadow-md shadow-inner">
                                                    {
                                                        item.status === "ACTIVE" ?
                                                            <div className="flex w-14 py-1 bg-green-700 rounded-full text-white font-bold ml-auto justify-center status-box">
                                                                {item.status}
                                                            </div>
                                                            :
                                                            <div className="flex w-14 py-1 bg-red-600 rounded-full text-white font-bold justify-center status-box">
                                                                {item.status}
                                                            </div>
                                                    }
                                                </div>
                                            </div>
                                        </td>
                                        <td className="border text-xs border-gray-300 w-1/12 px-4 py-2">
                                            <div className="w-full flex justify-center">
                                                <FontAwesomeIcon className="mt-1 mr-2" onClick={() => editModalOpen(index)} icon={faEdit} color="#6961ff" size="md" />
                                                <FontAwesomeIcon className="mt-1 ml-2" onClick={() => deleteModalOpen(index)} icon={faTrash} color="#f54242" size="md" />
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {/* Table End */}
                    {/* Edit Modal Start */}
                    {isEditModalOpen && (
                        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
                            <div className="bg-gray-700 p-6 rounded-lg shadow-lg w-96 relative">
                                {/* close button */}
                                <button
                                    className="absolute top-4 right-4 text-gray-300 hover:text-gray-500"
                                    onClick={() => setIsEditModalOpen(false)}
                                >
                                    &times;
                                </button>

                                {/* modal content */}
                                <h2 className="text-xl text-white text-center font-bold mb-4">Edit User</h2>
                                {/* form */}
                                <form onSubmit={editModalSubmit}>
                                    {/* name field */}
                                    <div className="mb-4">
                                        <label htmlFor="name" className="block text-sm font-medium text-gray-300">
                                            Name
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            placeholder="Enter name"
                                            className="shadow appearance-none bg-gray-900 text-sm border rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.name}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>
                                    {/* mobile no field */}
                                    <div className="mb-4">
                                        <label htmlFor="mobile_no" className="block text-sm font-medium text-gray-300">
                                            Mobile No
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="mobile_no"
                                            name="mobile_no"
                                            placeholder="Enter mobile no"
                                            className="shadow appearance-none bg-gray-900 text-sm border rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.mobile_no}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>
                                    {/* age no field */}
                                    <div className="mb-4">
                                        <label htmlFor="age" className="block text-sm font-medium text-gray-300">
                                            Age
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="age"
                                            name="age"
                                            placeholder="Enter age"
                                            className="shadow appearance-none bg-gray-900 text-sm border rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.age}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>
                                    {/* gender no field */}
                                    <div className="mb-4">
                                        <label htmlFor="gender" className="block text-sm font-medium text-gray-300">
                                            Gender
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <div className="flex items-center mt-2">
                                            <label className="mr-4 text-sm text-gray-300 lowercase">
                                                <input
                                                    type="radio"
                                                    id="gender"
                                                    name="gender"
                                                    value="male"
                                                    checked={formData.gender === "male"}
                                                    onChange={editModalChange}
                                                    className="mr-1"
                                                />
                                                Male
                                            </label>
                                            <label className="mr-4 text-sm text-gray-300 lowercase">
                                                <input
                                                    type="radio"
                                                    id="gender"
                                                    name="gender"
                                                    value="female"
                                                    checked={formData.gender === "female"}
                                                    onChange={editModalChange}
                                                    className="mr-1"
                                                />
                                                Female
                                            </label>
                                            <label className="text-sm text-gray-300 lowercase">
                                                <input
                                                    type="radio"
                                                    id="gender"
                                                    name="gender"
                                                    value="other"
                                                    checked={formData.gender === "other"}
                                                    onChange={editModalChange}
                                                    className="mr-1"
                                                />
                                                Other
                                            </label>
                                        </div>
                                    </div>

                                    {/* submit button */}
                                    <div className="flex justify-center mt-4">
                                        <button
                                            type="submit"
                                            className="mt-4 bg-blue-500 text-white px-4 py-2 text-xxs rounded font-bold uppercase"
                                        >
                                            Submit
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}
                    {/* Edit Modal End */}
                    {/* Delete Modal Start */}
                    {isDeleteModalOpen && (
                        <div className="fixed inset-0 bg-gray-900 bg-opacity-50 flex justify-center items-center">
                            <div className="bg-gray-700 p-6 rounded shadow-lg w-96 relative">
                                {/* close button */}
                                <button
                                    className="absolute top-4 right-4 text-gray-300 hover:text-gray-500"
                                    onClick={() => setIsDeleteModalOpen(false)}
                                >
                                    &times;
                                </button>
                                {/* model content */}
                                <h2 className="text-lg text-white font-bold mb-2">Are you sure?</h2>
                                <p className="mb-7 text-sm text-gray-300">Do you want to delete this user data?</p>
                                {/* yes or no button */}
                                <div className="flex justify-center space-x-4">
                                    <button
                                        onClick={() => setIsDeleteModalOpen(false)}
                                        className="borborder-red-500 text-white py-2 px-4 text-xxs font-bold uppercase rounded hover:bg-red-500"
                                    >
                                        No
                                    </button>
                                    <button
                                        onClick={deleteModalConfirm}
                                        className="bg-green-500 text-white py-2 px-4 text-xxs font-bold uppercase rounded hover:bg-green-600"
                                    >
                                        Yes
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                    {/* Delete Modal End */}
                </div>
            </div>
        </>
    );
}

export default UsersPage;
