import { useEffect, useState } from "react";
import NavbarComponent from "../components/Navbar";
import SidebarComponent from "../components/Sidebar";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCopy } from "@fortawesome/free-solid-svg-icons";


const PaymentDuesPage = () => {
    const all_payment_dues = [
        {
            "student_id": "FATHIMA_07465",
            "name": "fathima",
            "mobile_no": "1515151515",
            "due_amount": "80",
            "due_orders": [
                { "order_id": "215228606096", "order_total_amount": "80" }
            ],
            "due_start_date": "1736149943000", // This means array first data date, that is the start date
        },
        {
            "student_id": "RAHMAN_08976",
            "name": "rahman",
            "mobile_no": "3333333333",
            "due_amount": "210",
            "due_orders": [
                { "order_id": "563277582671", "order_total_amount": "50" },
                { "order_id": "827122314645", "order_total_amount": "120" },
                { "order_id": "982936652415", "order_total_amount": "40" }
            ],
            "due_start_date": "1736149933000", // This means array first data date, that is the start date
        },
        {
            "student_id": "KIRTHI_09845",
            "name": "kirthi",
            "mobile_no": "2222222222",
            "due_amount": "230",
            "due_orders": [
                { "order_id": "844128301060", "order_total_amount": "230" }

            ],
            "due_start_date": "1736149923000", // This means array first data date, that is the start date
        },
        {
            "student_id": "AKIF_00045",
            "name": "akif",
            "mobile_no": "4444444444",
            "due_amount": "530",
            "due_orders": [
                { "order_id": "887626166291", "order_total_amount": "160" },
                { "order_id": "302516207990", "order_total_amount": "260" },
                { "order_id": "433115833554", "order_total_amount": "110" }
            ],
            "due_start_date": "1736149913000", // This means array first data date, that is the start date
        },
        {
            "student_id": "ADITHYA_98084",
            "name": "adithya",
            "mobile_no": "5555555555",
            "due_amount": "140",
            "due_orders": [
                { "order_id": "935956125942", "order_total_amount": "60" },
                { "order_id": "398970536448", "order_total_amount": "80" }
            ],
            "due_start_date": "1736148913000", // This means array first data date, that is the start date
        },
        {
            "student_id": "IRSHAD_90321",
            "name": "irshad",
            "mobile_no": "6666666666",
            "due_amount": "30",
            "due_orders": [
                { "order_id": "560925663694", "order_total_amount": "30" },
            ],
            "due_start_date": "1736147913000", // This means array first data date, that is the start date
        },
        {
            "student_id": "BILAL_54692",
            "name": "bilal",
            "mobile_no": "1414141414",
            "due_amount": "160",
            "due_orders": [
                { "order_id": "987784169818", "order_total_amount": "65" },
                { "order_id": "506231675546", "order_total_amount": "30" },
                { "order_id": "583856156418", "order_total_amount": "25" },
                { "order_id": "830575854302", "order_total_amount": "40" }
            ],
            "due_start_date": "1736129913000", // This means array first data date, that is the start date
        },
        {
            "student_id": "MAJEED_00120",
            "name": "majeed",
            "mobile_no": "1616161616",
            "due_amount": "50",
            "due_orders": [
                { "order_id": "890069634276", "order_total_amount": "50" },
            ],
            "due_start_date": "1736119913000", // This means array first data date, that is the start date
        }
    ]
    // states
    const [refresh, setRefresh] = useState(false)
    const [isViewModalOpen, setIsViewModalOpen] = useState(false);
    const [selectedStudent, setSelectedStudent] = useState(null);
    const [payment_dues, setPaymentDues] = useState(all_payment_dues)

    // use effect
    useEffect(() => {
        console.log('Refreshed');
    }, [refresh]);

    // status change
    const changeStatus = (index, status) => {
        payment_dues[index].status = status
        setPaymentDues(payment_dues)
        setRefresh(!refresh)
    }

    // view modal open
    const viewModalOpen = (index) => {
        setSelectedStudent(all_payment_dues[index])
        setIsViewModalOpen(true)
    }

    // return
    return (
        <>
            <NavbarComponent now={'payment_dues'} />
            <SidebarComponent now={'payment_dues'} />
            <div className="w-auto min-h-screen px-3 pt-16 md:ml-60 lg:ml-80 bg-gray-900">
                <div className="w-full min-h-screen bg-gray-700 rounded-lg px-5 py-5 mt-3">
                    {/* Table */}
                    <div className="w-full bg-gray-900 overflow-x-auto rounded-xl">
                        <table className="w-full table-auto border-collapse border border-gray-400">
                            <thead>
                                <tr>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Student Id
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Name
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Mobile No
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Due Amount
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Due Orders
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Due Start Date
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Options
                                    </th>
                                </tr>
                            </thead>
                            <tbody>
                                {payment_dues.map((item, index) => (
                                    <tr key={index}>
                                        <td className="border text-white text-sm text-md border-gray-500 px-4 py-2">
                                            {item.student_id}
                                        </td>
                                        <td className="border text-white text-sm text-md border-gray-500 px-4 py-2">
                                            {item.name}
                                        </td>
                                        <td className="border text-white text-sm text-md border-gray-500 px-4 py-2">
                                            {item.mobile_no}
                                        </td>
                                        <td className="border text-white text-sm text-md border-gray-500 px-4 py-2">
                                            {item.due_amount}
                                        </td>
                                        <td onClick={() => viewModalOpen(index)} className="border text-white text-sm border-gray-500 px-4 py-2 hover:bg-gray-800 active:bg-gray-700">
                                            #{item.due_orders.length > 1 ? item.due_orders[0]?.order_id + " . . ." : item.due_orders[0]?.order_id }
                                        </td>
                                        <td className="border text-white text-sm text-md border-gray-500 px-4 py-2">
                                            {item.due_start_date}
                                        </td>
                                        {/* <td className="border text-white text-xs border-gray-500 px-4 py-2">
                                            <div className="w-full flex justify-center">
                                                {
                                                    item.status === "SUCCESS" ?
                                                        <div className="flex w-14 py-1 text-green-400 font-bold justify-center">
                                                            {item.status}
                                                        </div>
                                                        :
                                                        item.status === "FAILED" ?
                                                        <div className="flex w-14 py-1 text-red-400 font-bold justify-center">
                                                            {item.status}
                                                        </div>
                                                        :
                                                        item.status === "PARTIAL" ?
                                                        <div className="flex w-14 py-1 text-yellow-300 font-bold justify-center">
                                                            {item.status}
                                                        </div>
                                                        :
                                                        <div className="flex w-14 py-1 text-blue-400 font-bold justify-center">
                                                            {item.status}
                                                        </div>
                                                }
                                            </div>
                                        </td> */}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {/* Table */}
                    {/* View Modal Start */}
                    {isViewModalOpen && (
                        <div className="fixed inset-0 bg-gray-900 bg-opacity-50 flex justify-center items-center">
                            <div className="bg-gray-700 p-6 rounded shadow-lg w-96 relative">
                                {/* close button */}
                                <button
                                    className="absolute top-4 right-4 text-gray-300 hover:text-gray-500"
                                    onClick={() => setIsViewModalOpen(false)}
                                >
                                    &times;
                                </button>
                                {/* model content */}
                                <h2 className="text-lg text-white font-bold text-center mb-2">Due Orders</h2>
                                {/* dues order items */}
                                <div className="bg-gray-800 rounded p-4 mb-3">
                                    <ul className="space-y-2">
                                        {selectedStudent?.due_orders?.map((item, index) => (
                                            <li key={index} className="flex justify-between text-gray-300 text-xs">
                                                <span><FontAwesomeIcon className="mr-1 hover:text-gray-500 active:text-gray-400 cursor-pointer" onClick={() => navigator.clipboard.writeText(item.order_id)} icon={faCopy} color="#4B5563" size="md" />ORDER_ID: #{item.order_id}</span>
                                                <span>{item.order_total_amount}</span>
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                        </div>
                    )}
                    {/* View Modal End */}

                    <button popovertarget="my-popover" className="mt-6 bg-white">Open Popover</button>

                    <div popover id="my-popover" class="opacity-0 open:opacity-100 bg-white">
                        dddd
                    </div>


                </div>
            </div>
        </>
    );
}

