import { useEffect, useState } from "react";
import NavbarComponent from "../components/Navbar";
import SidebarComponent from "../components/Sidebar";
import { faAngleDown, faAngleUp, faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Dropdown from "../components/Dropdown";


const MenusPage = () => {
    const admin = localStorage.getItem("admin")
    const all_menus = [
        {
            "image": "data:image/jpeg;base64,",
            "name": "Menu 1",
            "status": "IN STOCK",
            "price": "10",
            "category": "Beverages",
            "rating": "4.8"
        },
        {
            "image": "data:image/jpeg;base64,",
            "name": "Menu 2",
            "status": "IN STOCK",
            "price": "12",
            "category": "Snacks",
            "rating": "4.5"
        },
        {
            "image": "data:image/jpeg;base64,",
            "name": "Menu 3",
            "status": "IN STOCK",
            "price": "15",
            "category": "Snacks",
            "rating": "4.3"
        },
        {
            "image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR3tufV9v3QaDl5gfFql4JITgj-awlcUg9IHp-4JDnqJquXwFM0snUTd9kCAA&s",
            "name": "Menu 4",
            "status": "OUT STOCK",
            "price": "150",
            "category": "Main Course",
            "rating": "4.7"
        },
        {
            "image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRhaauaqFbZ4EGEi8_u6Q-M4_yglMJYtbfcQU9gJEIs1YUwfw5gwuVuDjIDoQ&s",
            "name": "Menu 5",
            "status": "IN STOCK",
            "price": "30",
            "category": "Breakfast",
            "rating": "3.5"
        },
        {
            "image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQCvEGnZAusA6szh03JPZ7zByl1LR5l2vUPBG30C57tupBy6JP0A2I9OMsCHA&s",
            "name": "Menu 6",
            "status": "OUT STOCK",
            "price": "100",
            "category": "Main Course",
            "rating": "4.9"
        },
        {
            "image": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr3ODtK8HPnINXTY5-Aet7bQWg-VgZnY4MBd3oWC7QFzxUT-iSwBnD7BLiMw&s",
            "name": "Menu 7",
            "status": "IN STOCK",
            "price": "50",
            "category": "Beverages",
            "rating": "4.0"
        }
    ]

    // states
    const [refresh, setRefresh] = useState(false)
    const [selectedValue, setSelectedValue] = useState("");
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [index, setIndex] = useState(0);
    const [formData, setFormData] = useState({
        image: "",
        name: "",
        price: ""
    })
    const [menus, setMenus] = useState(all_menus)

    // use effect
    useEffect(() => {
        console.log('Refreshed');
    }, [refresh]);

    // category filtering
    useEffect(() => {
        if (selectedValue) {
            const filterd_menus = all_menus.filter(menu => menu.category.toLowerCase() === selectedValue.toLowerCase());
            setMenus(filterd_menus)
            setRefresh(!refresh)
        } else {
            setMenus(all_menus)
            setRefresh(!refresh)
        }
    }, [selectedValue]);

    // status change
    const changeStatus = (index, status) => {
        menus[index].status = status
        setMenus(menus)
        setRefresh(!refresh)
    }

    // search
    const search = (value) => {
        if (value) {
            const lowercase_value = value.toLowerCase();
            const filterd_menus = all_menus.filter(menu => menu.name.toLowerCase().includes(lowercase_value));
            setMenus(filterd_menus)
            setRefresh(!refresh)
        } else {
            setMenus(all_menus)
            setRefresh(!refresh)
        }
    }

    // edit modal open
    const editModalOpen = (index) => {
        setIndex(index)
        setIsEditModalOpen(true)
        setFormData(menus[index]);
    }

    // edit modal change
    const editModalChange = (e) => {
        const { name, type, value, files } = e.target;
        if (type === "file") {
            setFormData((prevData) => ({
                ...prevData,
                [name]: files && files[0] ? URL.createObjectURL(files[0]) : null,
            }));
        } else {
            setFormData((prevData) => ({
                ...prevData,
                [name]: value,
            }));
        }
    };

    // edit modal submit
    const editModalSubmit = (e) => {
        e.preventDefault();
        console.log("Form Data Submitted:", formData);
        menus[index] = formData
        setMenus(menus)
        setRefresh(!refresh)
        setIsEditModalOpen(false)
    };

    // delete modal open
    const deleteModalOpen = (index) => {
        setIndex(index)
        setIsDeleteModalOpen(true)
    }

    // edit modal confirm
    const deleteModalConfirm = () => {
        menus.splice(index, 1);
        setMenus(menus)
        setRefresh(!refresh)
        setIsDeleteModalOpen(false)
    };

    // return
    return (
        <>
            <NavbarComponent now={'menus'} />
            <SidebarComponent now={'menus'} />
            <div className="w-auto min-h-screen px-3 pt-16 md:ml-60 lg:ml-80 bg-gray-900">
                <div className="w-full min-h-screen bg-gray-700 rounded-lg px-5 py-5  mt-3">
                    {/* Filters */}
                    <div className="mb-3 flex w-full items-center">
                        {/* Dropdown Start */}
                        <div className="w-1/2 flex">
                            <Dropdown key_name={"category"} items={["Beverages", "Snacks", "Breakfast", "Main Course"]} selectedValue={selectedValue} setSelectedValue={setSelectedValue} />
                        </div>
                        {/* Dropdown End */}
                        {/* Add Menu */}
                    <div className="mb-3 flex w-full items-center">
                        <button onClick={deleteModalConfirm} className="border-2 border-green-500 text-green-500 hover:text-white py-1 px-6 text-xxs font-bold uppercase rounded hover:bg-green-500">
                            Add Menu
                        </button>
                    </div>
                        {/* Search Bar Start */}
                        <div className="w-1/2 flex justify-end">
                            <input
                                type="text"
                                id="search"
                                placeholder="Search..."
                                className="flex px-4 w-7/12 lg:w-4/12 py-1 bg-gray-900 border border-gray-300 rounded-full text-white text-xs lg:text-sm"
                                // value={search}
                                onChange={(e) => search(e.target.value)}
                            />
                        </div>
                        {/* Search Bar End */}
                    </div>
                    {/* Table */}
                    <div className="w-full bg-gray-900 overflow-x-auto rounded-xl">
                        <table className="w-full table-auto border-collapse border border-gray-400">
                            <thead>
                                <tr>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Image
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Name
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Price
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Category
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Rating
                                    </th>
                                    <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                        Status
                                    </th>
                                    {
                                        admin === "canteen" &&
                                        <th className="text-md border border-gray-500 px-4 py-2 bg-yellow-500 capitalize">
                                            Options
                                        </th>
                                    }
                                </tr>
                            </thead>
                            <tbody>
                                {menus.map((item, index) => (
                                    <tr key={index}>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            <img src={item.image} alt="Example" className="rounded-md shadow-lg h-9" />
                                        </td>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            {item.name}
                                        </td>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            ₹{item.price}
                                        </td>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            {item.category}
                                        </td>
                                        <td className="border text-white text-sm border-gray-500 px-4 py-2">
                                            ⭐️ {item.rating}
                                        </td>
                                        {
                                            admin === "canteen" ?
                                            <td className="border text-white text-xs border-gray-500 px-4 py-2">
                                                <div className="w-full flex justify-center">
                                                    <div onClick={() => changeStatus(index, item.status === "IN STOCK" ? "OUT STOCK" : "IN STOCK")} className="w-20 bg-gray-600 rounded-full shadow-md shadow-inner">
                                                        {
                                                            item.status === "IN STOCK" ?
                                                                <div className="flex w-14 py-1 bg-green-700 rounded-full text-white font-bold ml-auto justify-center status-box">
                                                                    {item.status}
                                                                </div>
                                                                :
                                                                <div className="flex w-14 py-1 bg-red-600 rounded-full text-white font-bold justify-center status-box">
                                                                    {item.status}
                                                                </div>
                                                        }
                                                    </div>
                                                </div>
                                            </td>
                                            :
                                            <td className={`border text-sm text-center text-md border-gray-500 px-4 py-2 font-semibold ${item.status === "IN STOCK" ? "text-green-400" : "text-red-400"}`}>
                                                {item.status}
                                            </td>
                                        }
                                        {
                                            admin === "canteen" &&
                                            <td className="border text-xs border-gray-300 w-1/12 px-4 py-2">
                                                <div className="w-full flex justify-center">
                                                    <FontAwesomeIcon className="mt-1 mr-2" onClick={() => editModalOpen(index)} icon={faEdit} color="#6961ff" size="md" />
                                                    <FontAwesomeIcon className="mt-1 ml-2" onClick={() => deleteModalOpen(index)} icon={faTrash} color="#f54242" size="md" />
                                                </div>
                                            </td>
                                        }
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {/* Table */}
                    {/* Edit Modal Start */}
                    {isEditModalOpen && (
                        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
                            <div className="bg-gray-700 p-6 rounded-lg shadow-lg w-96 relative">
                                {/* close button */}
                                <button
                                    className="absolute top-4 right-4 text-gray-300 hover:text-gray-500"
                                    onClick={() => setIsEditModalOpen(false)}
                                >
                                    &times;
                                </button>

                                {/* modal content */}
                                <h2 className="text-xl text-white text-center font-bold mb-4">Edit Menu</h2>
                                {/* form */}
                                <form onSubmit={editModalSubmit}>
                                    {/* image field */}
                                    <div className="mb-4">
                                        <label htmlFor="image" className="block text-sm font-medium text-gray-300">
                                            Image
                                            <span className="text-red-500">*</span>
                                        </label>
                                        {formData?.image ? (
                                            <img
                                                src={formData?.image}
                                                alt="Selected"
                                                className="mt-1 w-full object-cover rounded-md mb-3"
                                            />
                                        ) : (
                                            <p className="text-gray-500 text-sm">No image selected</p>
                                        )}
                                        <input
                                            type="file"
                                            id="image"
                                            name="image"
                                            className="hidden"
                                            onChange={editModalChange}
                                        />
                                        <label
                                            htmlFor="image"
                                            className="shadow appearance-none bg-gray-600 text-sm border border-gray-500 rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5 cursor-pointer hover:bg-gray-500 mb-3 text-center block"
                                        >
                                            Choose Image
                                        </label>
                                    </div>
                                    {/* name field */}
                                    <div className="mb-4">
                                        <label htmlFor="name" className="block text-sm font-medium text-gray-300">
                                            Name
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            placeholder="Enter name"
                                            className="shadow appearance-none bg-gray-900 text-sm border border-gray-500 rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.name}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>
                                    {/* price no field */}
                                    <div className="mb-4">
                                        <label htmlFor="price" className="block text-sm font-medium text-gray-300">
                                            Price
                                            <span className="text-red-500">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            id="price"
                                            name="price"
                                            placeholder="Enter price"
                                            className="shadow appearance-none bg-gray-900 text-sm border border-gray-500 rounded-md w-full py-1.5 px-3 text-gray-300 leading-tight focus:outline-none focus:shadow-outline mt-0.5"
                                            value={formData.price}
                                            onChange={editModalChange}
                                            required
                                        />
                                    </div>

                                    {/* submit button */}
                                    <div className="flex justify-center mt-4">
                                        <button
                                            type="submit"
                                            className="mt-4 border-2 border-blue-500 text-blue-500 hover:text-white px-4 py-2 text-xxs rounded font-bold uppercase hover:bg-blue-500"
                                        >
                                            Submit
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}
                    {/* Edit Modal End */}
                    {/* Delete Modal Start */}
                    {isDeleteModalOpen && (
                        <div className="fixed inset-0 bg-gray-900 bg-opacity-50 flex justify-center items-center">
                            <div className="bg-gray-700 p-6 rounded shadow-lg w-96 relative">
                                {/* close button */}
                                <button
                                    className="absolute top-4 right-4 text-gray-300 hover:text-gray-500"
                                    onClick={() => setIsDeleteModalOpen(false)}
                                >
                                    &times;
                                </button>
                                {/* model content */}
                                <h2 className="text-lg text-white font-bold mb-2">Are you sure?</h2>
                                <p className="mb-7 text-sm text-gray-300">Do you want to delete this menu data?</p>
                                {/* yes or no button */}
                                <div className="flex justify-center space-x-4">
                                    <button
                                        onClick={() => setIsDeleteModalOpen(false)}
                                        className="border-2 border-red-500 text-red-500 hover:text-white py-2 px-4 text-xxs font-bold uppercase rounded hover:bg-red-500"
                                    >
                                        No
                                    </button>
                                    <button
                                        onClick={deleteModalConfirm}
                                        className="border-2 border-green-500 text-green-500 hover:text-white py-2 px-4 text-xxs font-bold uppercase rounded hover:bg-green-500"
                                    >
                                        Yes
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                    {/* Delete Modal End */}
                </div>
            </div>
        </>
    );
}

export default MenusPage;
