import React from 'react';
import { faSignOutAlt, faBars } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useNavigate } from 'react-router-dom';
import SidebarComponent from './Sidebar';

function NavbarComponent() {
    // navigate
    const navigate = useNavigate()

    // render sidebar
    const renderSidebar = () => {
        return (
            <div className="hidden md:block md:w-60 lg:w-full max-w-80 min-h-screen fixed border border-right p-3 mt-16">
                <div className="w-full min-h-screen bg-gray-200 rounded-lg px-5 py-5">
                    <div onClick={() => {setCurrent('dashboard'); navigate('/')}} className="flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2">
                        <FontAwesomeIcon className="mt-1" icon={faTachometerAlt} color="black" size="md" />
                        <h1 className="text-md font-semibold text-black ml-2">Dashboard</h1>
                    </div>
                    <div onClick={() => {setCurrent('users'); navigate('/users')}} className="flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2">
                        <FontAwesomeIcon className="mt-1" icon={faUsers} color="black" size="md" />
                        <h1 className="text-md font-semibold text-black ml-2">Users</h1>
                    </div>
                    <div onClick={() => {setCurrent('categories'); navigate('/categories')}} className="flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2">
                        <FontAwesomeIcon className="mt-1" icon={faChartBar} color="black" size="md" />
                        <h1 className="text-md font-semibold text-black ml-2">Categories</h1>
                    </div>
                    <div onClick={() => {setCurrent('books'); navigate('/books')}} className="flex flex-row w-full hover:bg-gray-300 rounded-md mt-4 px-5 py-2">
                        <FontAwesomeIcon className="mt-1" icon={faBook} color={current === "books" ? "white" : "black"} size="md" />
                        <h1 className={current === "books" ? "text-md font-semibold text-white ml-2" : "text-md font-semibold text-black ml-2"}>Books</h1>
                    </div>
                </div>
            </div>
        )
    }

    // return
    return (
        <div className="flex flex-row w-full h-16 fixed bg-gray-800 px-9">
            <div className='flex justify-center items-center md:hidden'>
                <FontAwesomeIcon onClick={() => renderSidebar()} icon={faBars} color='white' size="lg" />
            </div>
            <div className="flex w-20 h-16 justify-center items-center ml-3">
                <h1 className="text-lg font-bold text-white">My App</h1>
            </div>
            <div className='flex justify-center items-center ml-auto'>
                <FontAwesomeIcon onClick={() => navigate('/login')} icon={faSignOutAlt} color='white' size="lg" />
            </div>
        </div>
    );
}

export default NavbarComponent;
